export const transformDate = (time: number): string => {
  let res = '';
  let t = (new Date().getTime() - Number(time)) / 1000;
  t = parseInt(t.toString(), 10);
  const day = Math.floor(t / (60 * 60 * 24));
  const hour = Math.floor(t / (60 * 60));
  const minute = Math.floor(t / 60);
  if (day > 0) return day + '天前';
  if (hour > 0) return hour + '小时前';
  if (minute > 0) return minute + '分钟前';
  return '刚刚';
};

// 日期格式化
export const dateFormat = (date: Date, format: string): string => {
  const args: any = {
    'M+': date.getMonth() + 1,
    'd+': date.getDate(),
    'h+': date.getHours(),
    'm+': date.getMinutes(),
    's+': date.getSeconds(),
    'q+': Math.floor((date.getMonth() + 3) / 3),
    'S': date.getMilliseconds()
  };
  if (/(y+)/.test(format)) {
    format = format.replace(RegExp.$1, (date.getFullYear() + '').substr(4 - RegExp.$1.length));
  }
  for (let i in args) {
    const n = args[i];
    if (new RegExp('(' + i + ')').test(format)) {
      format = format.replace(RegExp.$1, RegExp.$1.length === 1 ? n : ('00' + n).substr(('' + n).length));
    }
  }
  return format;
};

export const base64Trans: string = 'data:image/gif;base64,R0lGODlhAQABAJEAAAAAAP///////wAAACH5BAEAAAIALAAAAAABAAEAAAICVAEAOw==';

export const lazyImg = (): IntersectionObserver|undefined => { // 图片延迟加载
  const attr = 'data-src';
  const $img: Array<Element> = Array.prototype.slice.call(document.body.querySelectorAll(`img[${attr}]`));
  if (!$img.length) return;
  // 图片在可视区域，执行
  const complete = (target: Element) => {
    let src = target.getAttribute(attr);
    if (!src) return;
    target.setAttribute('src', src);
    target.removeAttribute(attr);
    target.removeAttribute('style');
  };
  const lazyObj = new IntersectionObserver((entries, observer) => {
    entries.forEach((entry) => {
      if (entry.intersectionRatio > 0) {
        const target = entry.target;
        observer.unobserve(target);
        complete(target);
      }
    });
  });
  $img.forEach((e) => {
    if (!e.getAttribute('src')) { // 设置透明背景
      e.setAttribute('src', base64Trans);
    }
    lazyObj.observe(e);
  });
  return lazyObj;
};

import { Component, Vue } from 'vue-property-decorator';
import { namespace } from 'vuex-class';
import { List, PullRefresh } from 'vant';
import { AxiosPromise } from 'axios';
import { transformDate } from '../utils/tools';
import * as types from '../store/mutation-types';

const newsModule = namespace('news'); // 获取命名空间

@Component({
  components: {
    List,
    PullRefresh
  }
})
export default class News extends Vue {
  private refreshLoad: boolean = false; // 上拉加载标识

  private moreLoad: boolean = false; // 下滑滚动加载标识

  @newsModule.State private newList!: Array<any>;

  @newsModule.State private ids!: Array<string>;

  @newsModule.State('init') private homeInit!: boolean;

  @newsModule.Action
  public fetchNewsList!: (params?: any) => AxiosPromise;

  @newsModule.Mutation(types.SET_NEWS_IDS)
  public setNewsIds!: () => void;

  private get finished(): boolean { // 新闻列表全部加载标识
    return !this.ids.length;
  }

  // 挂载之前操作
  public beforeMount(): void {
    if (!this.homeInit) { // 已初始化不再执行
      this.fetchNewsList(); // 加载腾讯新闻
    }
  }

  public transDate = transformDate;

  public onRefresh(): void { // 刷新新闻列表
    this.fetchNewsList({ _t: new Date().getTime() }).then(() => { // 加时间戳，防治接口缓存
      this.refreshLoad = false;
    });
  }

  public onLoadMore(): void { // 加载更多
    const ids = this.ids.slice(0, 20); // 获取前面20条id
    if (!ids || !ids.length) return;
    this.setNewsIds(); // 删掉前面20条数据
    this.fetchNewsList({ ids: ids.join(',') }).then(() => {
      this.moreLoad = false;
    });
  }

  public asyncData({ store }: any): void { // ssr初始化加载，数据预取
    return store.dispatch('news/fetchNewsList');
  }
}

