import Vue from 'vue';
import { sync } from 'vuex-router-sync';

import App from './App.vue';
import createRouter from './router';
import createStore from './store/index';

export default () => {
  const router = createRouter();
  const store = createStore();

  sync(store, router); // 同步路由状态(route state)到 store

  const app = new Vue({
    router,
    store,
    render: h => h(App)
  });
  return { app, router, store };
};

import createApp from '../src/createApp';

export default context => new Promise((resolve, reject) => {
  const { app, router, store } = createApp();
  // 设置服务器端 router 的位置
  router.push(context.url);
  // 等到 router 将可能的异步组件和钩子函数解析完
  router.onReady(() => {
    const matchedComponents = router.getMatchedComponents();
    // 设定title
    context.title = 'vue-ts-example';
    // context.title = router.history.current.name || 'app';
    // 匹配不到的路由，执行 reject 函数，并返回 404
    if (!matchedComponents.length) {
      return reject({ code: 404 });
    }
    // 对所有匹配的路由组件调用 `asyncData()`
    Promise.all(matchedComponents.map(Component => {
      const methods = Component.options.methods || {};
      if (methods.asyncData) { // 调用 asyncData
        return methods.asyncData({
          store,
          route: router.currentRoute
        });
      }
    })).then(() => {
      context.state = store.state;
      resolve(app);
    }).catch(reject);
  }, reject);
});
