import Vue from 'vue';
import Router, { RouteConfig, Route } from 'vue-router';

import Index from './views/Index.vue';
import News from './views/News.vue';
import Video from './views/Video.vue';
import Photo from './views/Photo.vue';
import Music from './views/Music.vue';
import Shopping from './views/Shopping.vue';
import NewsContent from './views/NewsContent.vue';

Vue.use(Router);

const routes: Array<RouteConfig> = [
  {
    path: '/',
    component: Index,
    children: [
      {
        path: '/',
        name: 'news',
        component: News,
        meta: {
          title: '腾讯要闻'
        }
      },
      {
        path: '/video.html',
        name: 'video',
        component: Video,
        meta: {
          title: '视频合集'
        }
      },
      {
        path: '/music.html',
        name: 'music',
        component: Music,
        meta: {
          title: '酷狗音乐'
        }
      },
      {
        path: '/photo.html',
        name: 'photo',
        component: Photo,
        meta: {
          title: '热点图片'
        }
      },
      {
        path: '/shopping.html',
        name: 'shopping',
        component: Shopping,
        meta: {
          title: '网购抢先'
        }
      }
    ]
  },
  {
    path: '/news/content/:id.html',
    name: 'newsContent',
    component: NewsContent
  }
];

// 路由切换的滚动行为
const scrollBehavior = (to: Route, from: Route, savedPosition: any) => {
  // 后退/前进时的位置
  if (savedPosition) {
    return savedPosition;
  } else {
    const position: any = {};
    // 需要滚动到顶部的单独配置
    let isToTop = to.matched.some((m: any) => m.meta.scrollToTop || m.meta.scrollToTop === undefined);
    if (isToTop) {
      position.x = 0;
      position.y = 0;
    }
    return position;
  }
};

export default () => {
  const router = new Router({
    mode: 'history',
    routes,
    scrollBehavior
  });
  // 只有在客户端才运行
  if (process.env.BUILD_ENV !== 'SSR') {
    router.beforeEach((to: Route, from: Route, next: any) => {
      const meta: any = to.meta || {};
      document.title = meta.title || 'vue-ts-example';
      next();
    });
  }

  return router;
};

import axios from 'axios';

const buildEnv = process.env.BUILD_ENV;
let apiRoot = '/';
if (buildEnv === 'SSR') {
  apiRoot = process.env.BUILD_SSR_URL || '/'; // SSR 地址
}

const http = axios.create({
  baseURL: apiRoot + 'API/',
  timeout: 30000
});

export default http;

import { ActionTree, MutationTree, Module } from 'vuex';
import { AxiosPromise } from 'axios';
import { RootState } from '../types';
import * as types from '../mutation-types';
import http from '../../utils/http';

interface NewList {
  title: string
  id: string
  source: string
  time: number
  img: string
  type: string
}

interface State { // 定义接口
  init: boolean
  newList: Array<NewList>
  ids: Array<string>
}

const state = (): State => ({ // 避免ssr数据公用，state 必须是一个函数
  init: false,
  newList: [],
  ids: []
});

const actions: ActionTree<State, RootState> = {
  fetchNewsList({ commit }, params): AxiosPromise<State> {
    params = params || {};
    return http.get('news/list', { params }).then(res => {
      const data = (res.data || {}).data;
      commit(types.SET_NEWS_DATA, data);
      return res;
    });
  },
  fetchNewContent({ commit }, id): AxiosPromise {
    return http.get('news/content', { params: { id }}).then(res => {
      const data = res.data || {};
      return data;
    });
  }
};

const mutations: MutationTree<State> = {
  [types.SET_NEWS_DATA](state, data) {
    state.init = true; // 已初始化
    const list: Array<NewList> = data.list || [];
    const ids: Array<string> = data.ids;
    if (ids && ids.length) {
      state.ids = ids; // 重置ids数组
      state.newList = []; // 重置新闻列表
    }
    list.forEach(item => {
      if (item.type === '0' || item.type === '56') { // 只筛选文章和视频
        state.newList.push(item);
      }
      // 剔除已加载的新闻
      const index = state.ids.indexOf(item.id);
      if (index > -1) {
        state.ids.splice(index, 1);
      }
    });
  },
  [types.SET_NEWS_IDS](state) {
    state.ids = state.ids.slice(20); // 设置ids
  }
};

const news: Module<State, RootState> = {
  namespaced: true,
  state,
  actions,
  mutations
};

export default news;

